mapper.update:
UPDATE matching.tb_match
   SET active_flag = '1'
 WHERE match_id = (SELECT sub_match.match_id
                     FROM (SELECT match_id
                             FROM matching.tb_match
                            WHERE sender = #{sender}
                              AND receiver = #{receiver}
                              AND active_flag = '0'
                          ) sub_match
                  )

UPDATE matching.tb_match
   SET active_flag = '2'
 WHERE match_id = (SELECT sub_match.match_id
                     FROM (SELECT match_id
                             FROM matching.tb_match
                            WHERE active_flag = '0'
                              AND sender = #{sender}
                              AND receiver = #{receiver}
                          ) sub_match
                  )

UPDATE matching.tb_match
   SET active_flag = '2'
 WHERE match_id = (SELECT sub_match.match_id
                     FROM (SELECT match_id
                             FROM matching.tb_match
                            WHERE active_flag = '1'
                              AND sender = #{sender}
                              AND receiver = #{receiver}
                          ) sub_match
                  )
